from .sql import Sqlfrom lagoupy.items import LagoupyItemclass LagoupyPipeline(object):    def process_item(self,item,spider):        if isinstance(item,LagoupyItem):            jobid=item['jobid']            ret=Sql.select_name(jobid)            if ret[0]==1:                print('已经存在了')            else:                jobid=item['jobid']                yaoqiu=item['yaoqiu']                Sql.insert_dd_name(jobid,yaoqiu)                print('开始存小说标题')