import mysql.connectorfrom lagoupy import settingsMYSQL_HOSTS=settings.MYSQL_HOSTSMYSQL_USER=settings.MYSQL_USERMYSQL_PASSWORD=settings.MYSQL_PASSWORDMYSQL_PORT=settings.MYSQL_PORTMYSQL_DB=settings.MYSQL_DBcnx=mysql.connector.connect(user=MYSQL_USER, password=MYSQL_PASSWORD, host=MYSQL_HOSTS, database=MYSQL_DB)cur=cnx.cursor(buffered=True)class Sql:    @classmethod    def insert_dd_name(cls,jobid,yaoqiu):        sql='insert into lagoupy(jobid,yaoqiu) values (\''+jobid+'\',\''+yaoqiu+'\')'        cur.execute(sql)        cnx.commit()    @classmethod    def select_name(cls,jobid):        sql='select exists (select 1 from lagoupy where jobid=\''+jobid+'\')'        cur.execute(sql)        return cur.fetchall()[0]